    def _parse_proxy(self, proxy_str):
        if not proxy_str:
            return None
        http_match = re.match(r'https?://([^:]+):([^@]+)@([^:]+:\d+)', proxy_str)
        if http_match:
            user, password, hostport = http_match.groups()
            return {
                'http': f'http://{user}:{password}@{hostport}',
                'https': f'http://{user}:{password}@{hostport}'
            }
        at_match = re.match(r'([^:]+):([^@]+)@([^:]+:\d+)', proxy_str)
        if at_match:
            user, password, hostport = at_match.groups()
            return {
                'http': f'http://{user}:{password}@{hostport}',
                'https': f'http://{user}:{password}@{hostport}'
            }
        parts = proxy_str.split(':')
        if len(parts) == 4:
            host, port, user, password = parts
            return {
                'http': f'http://{user}:{password}@{host}:{port}',
                'https': f'http://{user}:{password}@{host}:{port}'
            }
        if len(parts) == 2:
            host, port = parts
            return {
                'http': f'http://{host}:{port}',
                'https': f'http://{host}:{port}'
            }
        return None

