    # NopeCHA Captcha Solver
    # ============================================================================
    def _solve_captcha(self, sitekey, page_url):
        """
        Solve hCaptcha using NopeCHA API.
        Returns the solved token or None if failed.
        """
        api_key = "sub_1T2WdACRwBwvt6ptOS7dRb3Q"  # Your NopeCHA API key
        try:
            # Submit captcha to NopeCHA
            submit_url = "https://api.nopecha.com/"
            payload = {
                "key": api_key,
                "type": "hcaptcha",
                "sitekey": sitekey,
                "pageurl": page_url,
                "data": {}
            }
            response = requests.post(submit_url, json=payload, timeout=30)
            if response.status_code != 200:
                print(f"Captcha submission failed: {response.status_code}")
                return None
            result = response.json()
            if result.get("error"):
                print(f"Captcha error: {result['error']}")
                return None
            token = result.get("data")
            if token:
                return token
            else:
                # If not solved, maybe need to poll? NopeCHA is usually synchronous.
                return None
        except Exception as e:
            print(f"Exception solving captcha: {e}")
            return None

